{% load i18n %}

{% if request.user.is_anonymous or not request.user.is_authenticated %}
    <div class="direct-login-form {{ block.bootstrap_classes }}" id="direct-login-form-{{ block.id }}">
        <form action="{{ action }}" method="post" id="direct-login-form">
            <h3>Sign In</h3>
            <fieldset>
                {{ login_form.as_ul }}
            </fieldset>
            {% csrf_token %}
            <div class="actions pull-right">
                <button type="submit" class="direct-login-btn btn" value="login" name="{{ block.submit_btn_name }}">Login</button>
                <div class="clear"></div>
                <span class="pull-right pagination-right">
                    <a href="{% url 'password_reset' %}">lost password</a>
                </span>
                <div class="clear"></div>
            </div>
        </form>
    </div>
{% else %}
    <div class="direct-logged-in {{ block.bootstrap_classes }}" id="direct-logged-in-{{ block.id }}">
        <div class="centered">
            {% if request.user.is_disabled %}
                <h2>{% trans "Your account is disabled." %} </h2>
                <p><a href="{% url 'resend_activation' %}">Send me an activation link to bring back my account!</a></p>
            {% else %}
                {% with short_email=request.user.email|truncatechars:"20" %}
                {% with name_obj=request.user.get_full_name %}
                    <h3><small>Welcome</small> {% if not name_obj %}{{ short_email }}{% else %}<br/>{{ name_obj }}{% endif %}</h3>
                {% endwith %}
                {% endwith %}
            {% endif %}
            {% if not user.is_active %}
                <div class="alert-message info">
                    <p>{% trans "Please activate it using the activation link sent to your email. "%} <a href="{% url 'resend_activation' %}">{% trans "Resend my activation email." %}</a></p>
                </div>
            {% endif %}
            <a class="btn direct-logout-btn" href="{% url 'auth_logout' %}">Log Out</a>
        </div>
    </div>
{% endif %}

{% load i18n %}

<div class="direct-content-block {{ block.bootstrap_classes }}" id="direct-content-block-{{block.id}}">
<style>
.direct-myjobs-logo {
    background: transparent url('//d2e48ltfsb5exy.cloudfront.net/myjobs/logos/myjobs_logo.145-03.png') 120px 0 no-repeat;
    text-indent: -9000px;
    height: 149px;
}
.direct-myjobs-tagline {
    text-indent: 120px;
    color: #a95051;
    text-shadow: 1px 1px #ccc;
}
</style>

<div class="centered">
    <h1 class="direct-myjobs-logo">My.jobs</h1>
    <h2 class="direct-myjobs-tagline">The Right Place For<a></a><noscript> You.</noscript></h2>
</div>

</div>

{% load i18n %}

{% if request.user.is_anonymous %}
    <div class="direct-registration-form {{ block.bootstrap_classes }}" id="direct-registration-form-{{ block.id }}">
        <form action="{{ action }}" method="post" id="direct-registration-form">
            <h3>Sign Up</h3>
            <fieldset>
                {{ registration_form.as_ul }}
            </fieldset>
            {% csrf_token %}
            <div class="actions pull-right">
                <button type="submit" class="direct-registration-btn btn" name="{{ block.submit_btn_name }}" id="registration">Create Account</button>
                <div class="clear"></div>
            </div>
        </form>
    </div>
{% endif %}
